sources.styles/utils/_reset.scss:
body,
h1,
h2,
h3,
ul,
p {
  margin: 0;
}

sources.styles/blocks/page.scss:
.page {
  font-family: "Poppins", sans-serif;
  font-weight: 400;
  font-size: 16px;
  line-height: 140%;
  color: #fff;

  scroll-behavior: smooth;
  background-color: #1d1d1d;

  &__body--with-menu {
    overflow: hidden;
  }

  &__menu {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1;

    transform: translateX(-100%);
    transition: transform 0.5s;

    &:target {
      transform: translateX(0);
    }
  }

  &__section-title {
    font-weight: 500;
    font-size: 32px;
    line-height: 100%;
    letter-spacing: -1.5px;
    text-align: center;

    margin-top: 60px;

    @include onTablet {
      font-size: 48px;
      margin-top: 120px;
    }

    @include onDescktop {
      font-size: 64px;
    }
  }

  &__section-text {
    font-weight: 500;
    font-size: 23px;
    line-height: 140%;
    margin-top: 32px;

    @include onTablet {
      margin-top: 120px;
    }
  }

  &__section-button {
    margin-top: 40px;

    @include onTablet {
      margin-top: 48px;
    }
  }
}

sources.styles/blocks/container.scss:
.container {
  margin: 0 auto;
  max-width: 1020px;
  padding: 0 20px;

  @include onTablet {
    padding: 0 72px;
  }

  @include onDescktop {
    padding: 0 120px;
  }
}

sources.styles/blocks/icon.scss:
.icon {
  display: block;
  width: 100%;
  height: 0;
  padding-bottom: 100%;

  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;

  &--menu {
    background-image: url(../images/menu.svg);
    transition: background-image 0.5s;

    &:hover {
      background-image: url(../images/menu_hover.svg);
    }
  }

  &--phone {
    background-image: url(../images/phone.svg);
  }

  &--cross {
    background-image: url(../images/cross.svg);
  }
}

sources.styles/blocks/top-actions.scss:
.top-actions {
  --icon-width: 24px;

  display: grid;
  grid-template-columns: 1fr var(--icon-width) var(--icon-width);
  justify-items: start;
  align-items: center;
  gap: 32px;

  @include onTablet {
    --icon-width: 32px;

  }
}

sources.styles/blocks/header.scss:
.header {
  background-image: url(../images/header_mobile.jpg);
  background-repeat: no-repeat;
  background-size: cover;

  @include onTablet {
    background-image: url(../images/header_tablet.jpg);
  }

  @include onDescktop {
    background-image: url(../images/header_descktop.jpg);
  }

  &__content {
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    height: 100vh;
    box-sizing: border-box;
    padding: $headerTopPadding 0 88px;

    @include onTablet {
      padding-bottom: 130px;
    }

    @include onDescktop {
      padding-bottom: 200px;
    }
  }

  &__title {
    font-weight: 700;
    font-size: 36px;
    line-height: 100%;
    letter-spacing: -1.5px;
    text-align: center;
    color: #fff;

    @include onTablet {
      font-size: 64px;
    }

    @include onDescktop {
      font-size: 80px;
    }
  }

  &__interaction {
    display: flex;
    gap: 30px;
  }

  &__call {
    text-decoration: none;
    line-height: 0;

    @include onDescktop {
      &::before {
        content: "+1 234 555-55-55";
        position: relative;
        top: 50%;
        transform: translateX(-100%);

        display: block;
        width: max-content;
        padding-right: 12px;

        font-weight: 700;
        font-size: 12px;
        line-height: 16px;
        color: #fff;
        letter-spacing: 2px;
        text-transform: uppercase;
        opacity: 0;
        pointer-events: none;
        transition: opacity 0.5s;
      }

      &:hover::before {
        opacity: 1;
        pointer-events: all;
      }
    }
  }
}

sources.styles/blocks/menu.scss:
.menu {
  background-color: $colorMenu;
  text-transform: uppercase;

  &__content {
    box-sizing: border-box;
    height: 100vh;
    padding: $headerTopPadding 4px;

    overflow-y: auto;
  }

  &__top {
    margin-bottom: 44px;
  }

  &__list {
    list-style: none;
    padding: 0;
    margin-bottom: 44px;
  }

  &__item {
    margin-bottom: 24px;

    &:last-child {
      margin-bottom: 0;
    }
  }

  &__link {
    display: inline-block;
    padding-bottom: 8px;
    border-bottom: 1px solid transparent;

    color: $colorText;
    font-weight: 600;
    font-size: 22px;
    line-height: 22px;
    letter-spacing: 2px;
    text-decoration: none;

    transition: border-color 0.5s;

    @include onDescktop {
      font-weight: 700;
    }

    &:hover {
      border-color: $colorText;
    }
  }

  &__call {
    display: block;
    width: max-content;

    font-weight: 600;
    color: $colorText;
    text-decoration: none;

    &--phone {
      font-size: 16px;
      line-height: 27px;
      margin-bottom: 16px;
    }

    &--action {
      font-size: 12px;
      line-height: 16px;
      letter-spacing: 2px;
      padding-bottom: 8px;
      border-bottom: 1px solid $colorText;
    }
  }
}

sources.styles/blocks/footer.scss:
.footer {
  background-image: url(../images/footer.jpg);
  height: 200px;
  background-size: cover;
  background-position: center;
  margin-top: 120px;

  @include onTablet {
    height: 480px;
  }

  @include onDescktop {
    height: 555px;
    margin-top: 182px;
  }
}

sources.styles/blocks/card.scss:
.card {
  display: flex;
  flex-direction: column;
  max-width: 280px;

  @include onTablet {
    max-width: 392px;
  }

  @include onDescktop {
    max-width: 324px;
  }

  color: #fff;

  &__picture {
    margin-top: 48px;
    @include onDescktop {
      @include hover (transform, scale(1.2, 1.2));
    }
  }

  &__name {
    font-weight: 700;
    font-size: 24px;
    line-height: 140%;

    margin-top: 32px;
  }

  &__text {
    margin-top: 8px;
  }

  &__price {
    font-weight: 700;
    font-size: 24px;
    line-height: 140%;

    margin-top: 16px;
  }
}

sources.styles/blocks/grid.scss:
.grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 20px;

  &--center {
    justify-items: center;
  }

  @include onTablet {
    grid-template-columns: repeat(6, 1fr);
    column-gap: 24px;
  }

  @include onDescktop {
    grid-template-columns: repeat(12, 1fr);
    column-gap: 24px;
  }

  &__item {
    @for $start from 1 through 2 {
      @for $end from $start through 2 {
        &--#{$start}-#{$end} {
          grid-column: #{$start} / #{$end + 1};
        }
      }
    }
    @include onTablet {
      @for $start from 1 through 6 {
        @for $end from $start through 6 {
          &--tablet-#{$start}-#{$end} {
            grid-column: #{$start} / #{$end + 1};
          }
        }
      }
    }
    @include onDescktop {
      @for $start from 1 through 12 {
        @for $end from $start through 12 {
          &--descktop-#{$start}-#{$end} {
            grid-column: #{$start} / #{$end + 1};
          }
        }
      }
    }
  }

}

sources.styles/blocks/place.scss:
.place {

  &__photo-container {
    margin-top: 40px;

    position: relative;

    box-sizing: border-box;
    width: 100%;
    min-height: 187px;
    overflow: hidden;

    &::after {
      content: "";
      display: block;
      padding-bottom: 100%;
    }

    @include onTablet {
      margin-top: 48px;

      height: 187px;
      padding-bottom: 0;
    }

    @include onDescktop {
      height: 320px;
    }
  }

  &__photo {
    position: absolute;
    width: 100%;
    height: 100%;

    object-fit: cover;
    object-position: center;

    transition: transform 0.5s;

    @include onDescktop {
      &:hover {
        transform: scale(1.2, 1.2);
      }
    }
  }

  &__title {
    font-weight: 700;
    font-size: 24px;
    line-height: 140%;

    margin-top: 16px;
  }

  &__text {
    margin-top: 8px;
  }
}

sources.styles/blocks/button.scss:
.button {
  display: flex;
  align-items: center;
  justify-content: center;

  box-sizing: border-box;
  height: 56px;
  width: 100%;

  background-color: #fff;
  border-radius: 40px;

  color: $colorText;
  text-decoration: none;
  font-weight: 700;
  font-size: 16px;
  line-height: 27px;
  text-align: center;

  transition: all 0.5s;

  @include hover (box-shadow, 1px 4px 16px rgba(232, 232, 240, 0.4));

  &:active {
    box-shadow: 1px 4px 16px rgba(232, 232, 240, 0.64);
  }
}

sources.styles/blocks/contacts.scss:
.contacts {
  &__title {
    font-weight: 500;
    font-size: 32px;
    line-height: 32px;
    text-align: center;
    letter-spacing: -1.5px;

    margin-top: 80px;
    margin-bottom: 48px;

    @include onTablet {
      margin-top: 120px;
    }

    @include onDescktop {
      margin-top: 180px;
    }
  }

  &__form {
    display: flex;
    flex-direction: column;

  }

  &__form-input {
    box-sizing: border-box;

    display: block;
    width: 100%;
    height: 48px;
    margin-bottom: 16px;

    background: #f8f8fa;
    border: 1px solid transparent;
    border-radius: 40px;
    padding: 14px 16px;

    &::placeholder {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #7e7e83;
    }

    &:hover {
      border-color: #d5d7de;
      color: #292929;
    }

    &:focus {
      border-color: #d5d7de;
      color: #292929;
      outline: none;
    }

  }

  &__form-textarea {
    font-family: inherit;
    padding: 14px 16px;
    background: #f8f8fa;
    border-radius: 16px;
    height: 148px;
    resize: none;

    margin-bottom: 16px;

    &::placeholder {
      font-weight: 400;
      font-size: 14px;
      line-height: 20px;
      color: #7e7e83;
    }

    &:focus {
      outline: none;
    }
  }

  &__form-button {
    border: 1px solid transparent;
    margin-bottom: 48px;
    cursor: pointer;

    @include onTablet {
      margin-bottom: 0;
    }
  }

  &__info-title {
    font-size: 14px;
    line-height: 140%;

    margin-bottom: 8px;
  }

  &__info-link {
    display: block;

    font-weight: 500;
    text-decoration: none;
    color: #fff;

    margin-bottom: 24px;

    &:hover {
      color: $colorMenu;
    }
  }
}
